package ch04.ex01;

public class P172 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j = 1;
		int sum = 0;
		int sum2 = 0;
		
		for(int i=1; i<=10; i++) {
			
			sum = sum + i;
			
			
		}
		System.out.println(sum);
		
		while( j <= 10) {
			sum2 = sum2 + j;
			
		}
		
		System.out.println(sum2);
		
		
	}

}
